module testbench;
    reg clk, reset, w;
    wire z;
    reg [3:0] test_state;
    reg error_flag;

    // DUT instantiation
    top_module DUT (
        .clk(clk),
        .reset(reset),
        .w(w),
        .z(z)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Toggle clock every 5ns
    end

    // Stimulus
    initial begin
        reset = 1; w = 0; test_state = 0; error_flag = 0;
        #10 reset = 0; // Release reset

        // Test state transitions as per the state diagram
        // State A transitions
        #10 check_state("A", 0, 1);  // A --0--> B
        #10 check_state("B", 0, 1);  // B --0--> C
        #10 check_state("C", 0, 0);  // C --0--> E
        #10 check_state("E", 1, 1);  // E --1--> D
        #10 check_state("D", 0, 1);  // D --0--> F
        #10 check_state("F", 1, 0);  // F --1--> C
        #10 check_state("C", 0, 1);  // C --1--> D
        #10 check_state("D", 0, 0);  // D --1--> A
        #10 check_state("A", 0, 1);  // A --1--> A

        // Repeating transitions for robustness
        #10 check_state("A", 0, 0);  // A --0--> B
        #10 check_state("B", 0, 0);  // B --1--> D
        #10 check_state("D", 0, 0);  // D --1--> A

        // Check final error flag and display result
        #10 if (!error_flag) $display("all test cases passed");

        // Finish the test
        #10 $finish;
    end

    // Task to check state transition and update error flag if necessary
    task check_state;
        input [7:0] exp_state;
        input exp_z;
        input w_in;
        begin
            w = w_in;
            @(posedge clk);
            if (z !== exp_z) begin
                error_flag = 1;
            end
            @(posedge clk);
        end
    endtask

endmodule

