module testbench;
    reg clk;
    reg in;
    reg reset;
    wire out;

    reg tests_failed; // Flag to indicate if any tests failed

    top_module DUT (
        .clk(clk),
        .in(in),
        .reset(reset),
        .out(out)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = !clk; // Generate a clock with 10ns period
    end

    // Test cases
    initial begin
        tests_failed = 0; // Initialize the flag to 0 (no failures)

        // Initialize signals
        reset = 1; in = 0; #10; // Apply reset
        reset = 0;           #10;

        // Test case 1: Reset state, B with out=1
        if (out !== 1) begin
            $display("Test case 1 failed");
            tests_failed = 1;
        end

        // Transition to A, in=0
        in = 0; #10;
        // Test case 2: State A, out should be 0
        if (out !== 0) begin
            $display("Test case 2 failed");
            tests_failed = 1;
        end

        // Stay in A, in=1
        in = 1; #10;
        // Test case 3: Stay in A, out should still be 0
        if (out !== 0) begin
            $display("Test case 3 failed");
            tests_failed = 1;
        end

        // Go back to B, in=0
        in = 0; #10;
        // Test case 4: Back to B, out should be 1
        if (out !== 1) begin
            $display("Test case 4 failed");
            tests_failed = 1;
        end

        // Stay in B, in=1
        in = 1; #10;
        // Test case 5: Stay in B, out should still be 1
        if (out !== 1) begin
            $display("Test case 5 failed");
            tests_failed = 1;
        end

        // Final check if all test cases passed
        if (!$test$plusargs("quiet")) begin
            if (!tests_failed) $display("All test cases passed");
        end

        $finish; // Terminate the simulation
    end
endmodule

