module tb_top_module;

    // Inputs
    reg clk;
    reg in;
    reg areset;

    // Outputs
    wire out;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .in(in),
        .areset(areset),
        .out(out)
    );

    // Variables
    integer failed_tests = 0;

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // 100MHz Clock
    end

    // Test cases
    initial begin
        // Initialize Inputs
        in = 0;
        areset = 1;  // Apply reset

        // Wait for global reset to finish
        #10;
        areset = 0;  // Release reset

        // Default state should be A with output 0
        if (out !== 0) begin
            $display("Test case failed at state A with output 0");
            failed_tests = failed_tests + 1;
        end

        // Case 1: State A (out=0) and input 0 -> Stay in A (out=0)
        #10; in = 0;
        #10; if (out !== 0) begin
            $display("Test case failed at state A with input 0");
            failed_tests = failed_tests + 1;
        end

        // Case 2: From A (input 1) -> Transition to B (out=0)
        #10; in = 1;
        #10; if (out !== 0) begin
            $display("Test case failed at state B from A with input 1");
            failed_tests = failed_tests + 1;
        end

        // Case 3: From B (input 1) -> Stay in B (out=0)
        #10; in = 1;
        #10; if (out !== 0) begin
            $display("Test case failed at state B with input 1");
            failed_tests = failed_tests + 1;
        end

        // Case 4: From B (input 0) -> Transition to C (out=0)
        #10; in = 0;
        #10; if (out !== 0) begin
            $display("Test case failed at state C from B with input 0");
            failed_tests = failed_tests + 1;
        end

        // Case 5: From C (input 1) -> Transition to D (out=1)
        #10; in = 1;
        #10; if (out !== 1) begin
            $display("Test case failed at state D from C with input 1");
            failed_tests = failed_tests + 1;
        end

        // Case 6: From D (input 0) -> Transition to C (out=0)
        #10; in = 0;
        #10; if (out !== 0) begin
            $display("Test case failed at D transitioning to C with input 0");
            failed_tests = failed_tests + 1;
        end

        // Case 7: Asynchronous reset check
        #10; areset = 1;
        #10; if (out !== 0) begin
            $display("Test case failed during asynchronous reset");
            failed_tests = failed_tests + 1;
        end
        #10; areset = 0;

        // Check all test cases
        #10;
        if (failed_tests == 0)
            $display("all test cases passed");
        else
            $display("%d test cases failed", failed_tests);

        // Finish the simulation
        #20; $finish;
    end

endmodule

