        forever #5 clk = ~clk; // 100MHz clock, 10ns period
    end

    // Helper task for sending a bit with delay
    task send_bit;
        input bit_value;
        begin
            in = bit_value;
            #10; // wait for one bit-period
        end
    endtask

    // Test cases
    initial begin
        reset = 1'b1; // applying reset
        #20;
        reset = 1'b0;
        #20;

        // Case 1: Valid 8-bit data byte "01010101" with correct start and stop bits
        send_bit(0); // Start bit
        send_bit(1); // LSB
        send_bit(0);
        send_bit(1);
        send_bit(0);
        send_bit(1);
        send_bit(0);
        send_bit(1);
        send_bit(0); // MSB
        send_bit(1); // Stop bit
        #10;

        // Case 2: Invalid sequence, missing stop bit
        send_bit(0); // Start bit
        send_bit(1); // LSB
        send_bit(0);
        send_bit(1);
        send_bit(0);
        send_bit(1);
        send_bit(0);
        send_bit(1);
        send_bit(0); // MSB
        send_bit(0); // Wrong stop bit
        #10; 
        send_bit(1); // Recovery by sending a correct stop bit
        #10;

        // Case 3: Correct byte after previous error
        send_bit(0); // Start bit
        send_bit(0); // LSB
        send_bit(1);
        send_bit(0);
        send_bit(1);
        send_bit(0);
        send_bit(1);
        send_bit(0);
        send_bit(1); // MSB
        send_bit(1); // Stop bit
        #10; // Delay before ending the simulation

        // Check results
        if(done === 1'b1) begin
            $display("all test cases passed");
        end

        $finish; // terminate the simulation
    end
endmodule

