        mode = 0;
        too_cold = 0;
        too_hot = 0;
        fan_on = 0;

        // Monitor changes
        $monitor("At time %t, mode = %b, too_cold = %b, too_hot = %b, fan_on = %b, heater = %b, aircon = %b, fan = %b", $time, mode, too_cold, too_hot, fan_on, heater, aircon, fan);

