    top_module uut (
        .clk(clk),
        .resetn(resetn),
        .in(in),
        .out(out)
    );

    // Generate clock with period 10 ns
    always begin
        clk = 0; 
        #5; 
        clk = 1; 
        #5; 
    end

    // Test cases
    initial begin
        // Initialize Inputs
        in = 0;
        resetn = 0; // active low reset
        #10; 

        // Deactivate reset
        resetn = 1;

        // Test Case 1: Check reset functionality
        if (out !== 0) begin
            $display("Test Case 1 Failed: Output should be 0 after reset.");
            $finish;
        end

        // Input sequence
        in = 1;
        #20; // cycle clk to shift input
        in = 0;
        #10;
        in = 1;
        #10;
        in = 0;
        #20;

        // Expected Output: 0100 -> last input first out due to shift-right operation
        // Shifted value should now start to appear at out
        if (out !== 1) begin
            $display("Test Case 2 Failed: Output did not match expected value.");
            $finish;
        end

        // Continue testing shifted values
        #10; // This should shift the next '0' into the output
        if (out !== 0) begin
            $display("Test Case 3 Failed: Output did not match expected value.");
            $finish;
        end

        #10; // Next '1' should come at output
        if (out !== 1) begin
            $display("Test Case 4 Failed: Output did not match expected value.");
            $finish;
        end

        #10; // Next '0' should come at output
        if (out !== 0) begin
            $display("Test Case 5 Failed: Output did not match expected value.");
            $finish;
        end

        #10; // Finally, last remains zero as expected in cycle.
        if (out !== 0) begin
            $display("Test Case 6 Failed: Output did not match expected value.");
            $finish;
        end

        $display("all test cases passed");
    end
