module tb_top_module;

    // Inputs
    reg clk;
    reg resetn;
    reg x;
    reg y;

    // Outputs
    wire f;
    wire g;

    // Internal registers for testing
    reg f_reg;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .resetn(resetn),
        .x(x),
        .y(y),
        .f(f),
        .g(g)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock with period of 10ns
    end

    // Test vectors
    initial begin
        // Initialize inputs
        resetn = 0; x = 0; y = 0;

        // Reset input
        #10;
        resetn = 1;

        // Scenario: g should be set to 1 permanently if y is 1 within 2 clock cycles
        @(posedge clk) f_reg = 0;
        @(posedge clk) f_reg = 0;
        @(posedge clk) f_reg = 0;
        @(posedge clk) x = 1;
        @(posedge clk) x = 0; // x sequence starts
        @(posedge clk) x = 1; // x = 1, 0, 1...
        @(posedge clk) x = 0; // ...1
        @(posedge clk) y = 0;
        @(posedge clk) y = 1; // y = 1 within 2 clocks, should set g = 1 permanently
        #20;

        // Check if g is set to 1 permanently
        if (g !== 1) begin
            $display("Test case failed: g not set to 1 permanently");
            $finish;
        end

        // Scenario: g should be set to 0 permanently if y is not 1 within 2 clock cycles
        resetn = 0;
        #10;
        resetn = 1;
        x = 0;
        @(posedge clk);
        @(posedge clk) x = 1;
        @(posedge clk) x = 0; // x sequence starts again
        @(posedge clk) x = 1; // x = 1, 0, 1...
        @(posedge clk) x = 0; // ...1
        @(posedge clk) y = 0;
        @(posedge clk) y = 0; // y not 1 within 2 clocks, g should be 0 permanently
        @(posedge clk);

        // Check if g is set to 0 permanently
        if (g !== 0) begin
            $display("Test case failed: g not set to 0 permanently");
            $finish;
        end

        // If no failures, pass message
        $display("all test cases passed");
    end
endmodule

