    assign mm = mm_control;
    assign ss = ss_control;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .reset(reset),
        .ena(ena),
        .pm(pm),
        .hh(hh),
        .mm(mm),
        .ss(ss)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generating a clock with period 10ns
    end

    // Test scenarios
    initial begin
        // Initialize inputs
        reset = 1;
        ena = 0;
        hh_control = 8'h00;
        mm_control = 8'h00;
        ss_control = 8'h00;
        #20;        // Wait to ensure the clock is properly reset

        reset = 0;  // Release reset
        @(posedge clk); // Align with clock
        ena = 1;    // Enable counting

        // Test 0: Check reset functionality
        reset = 1;
        #10;
        if (hh !== 8'h12 || mm !== 8'h00 || ss !== 8'h00 || pm !== 0) begin
            $display("Test case failed: Reset functionality");
            $finish;
        end
        reset = 0;

        // Test 1: Normal counting
        ena = 1;
        repeat (10) @(posedge clk); // assume this gets us to 10 seconds
        if (hh !== 8'h12 || mm !== 8'h00 || ss !== 8'h10) begin
            $display("Test case failed: Normal counting");
            $finish;
        end

        // Test 2: Minute rollover
        ss_control = 8'h59;
        @(posedge clk);
        if (mm !== 8'h01 || ss !== 8'h00) begin
            $display("Test case failed: Minute rollover");
            $finish;
        end

        // Test 3: Hour rollover
        mm_control = 8'h59; ss_control = 8'h59;
        @(posedge clk);
        if (hh !== 8'h01 || mm !== 8'h00 || ss !== 8'h00) begin
            $display("Test case failed: Hour rollover");
            $finish;
        end

        // Test 4: PM/AM toggle
        hh_control = 8'h11; mm_control = 8'h59; ss_control = 8'h59;
        @(posedge clk);
        if (pm !== 1) begin
            $display("Test case failed: PM toggle");
            $finish;
        end
        @(posedge clk); // Flip to AM (12AM)
        if (pm !== 0) begin
            $display("Test case failed: AM toggle");
            $finish;
        end

        // All test cases passed
        $display("all test cases passed");

        $finish;
    end

endmodule

