module testbench;
    reg clk, shift_ena, count_ena, data;
    wire [3:0] q;
    reg [3:0] expected_q;
    integer i, error_flag;

    top_module uut (
        .clk(clk),
        .shift_ena(shift_ena),
        .count_ena(count_ena),
        .data(data),
        .q(q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10ns
    end

    // Test procedure
    initial begin
        // Initial conditions
        shift_ena = 0;
        count_ena = 0;
        data = 0;
        error_flag = 0;

        // Reset condition
        #10;
        check_expected("Initial reset", 4'b0000);

        // Test shifting
        shift_ena = 1;
        count_ena = 0;
        for (i = 0; i < 4; i = i + 1) begin
            data = i % 2;  // Send alternating bit pattern 0,1,0,1
            #10;
            expected_q = {expected_q[2:0], data};
            check_expected($sformatf("Shift in: %b", data), expected_q);
        end

        // Test decrement
        shift_ena = 0;
        count_ena = 1;
        for (i = 0; i < 4; i = i + 1) begin
            #10;
            expected_q = expected_q - 1;
            check_expected("Decrement", expected_q);
        end

        // Make sure at least one case does not affect the output
        shift_ena = 1;
        count_ena = 1;
        #10;
        check_expected("Undefined behavior (shift_ena and count_ena both high)", expected_q);

        // Finish testing
        #10;
        if (error_flag == 0) begin
            $display("all test cases passed");
        end

        $finish;
    end

    // Procedure to compare expected and actual results
    task check_expected;
        input string testname;
        input [3:0] expected_value;
        begin
            if (q !== expected_value) begin
                $display("[%0t ns] TEST FAILED: %s. Expected %b, got %b.", $time, testname, expected_value, q);
                error_flag = 1;
            end
        end
    endtask
endmodule

