module testbench;
    reg a, b;
    wire out_and, out_or, out_xor, out_nand, out_nor, out_xnor, out_anotb;

    // Instantiate the DUT
    top_module DUT (
        .a(a),
        .b(b),
        .out_and(out_and),
        .out_or(out_or),
        .out_xor(out_xor),
        .out_nand(out_nand),
        .out_nor(out_nor),
        .out_xnor(out_xnor),
        .out_anotb(out_anotb)
    );

    // Test case variables
    integer errors = 0;
    integer testcases = 0;

    // Function to apply test vectors and check results
    task apply_test;
        input reg a_test, b_test;
        input expected_and, expected_or, expected_xor, expected_nand, expected_nor, expected_xnor, expected_anotb;

        begin
            a = a_test;
            b = b_test;
            #10; // Wait for propagation

            if (out_and !== expected_and) begin
                $display("Error: AND failed for a=%b, b=%b", a_test, b_test);
                errors = errors + 1;
            end

            if (out_or !== expected_or) begin
                $display("Error: OR failed for a=%b, b=%b", a_test, b_test);
                errors = errors + 1;
            end

            if (out_xor !== expected_xor) begin
                $display("Error: XOR failed for a=%b, b=%b", a_test, b_test);
                errors = errors + 1;
            end

            if (out_nand !== expected_nand) begin
                $display("Error: NAND failed for a=%b, b=%b", a_test, b_test);
                errors = errors + 1;
            end

            if (out_nor !== expected_nor) begin
                $display("Error: NOR failed for a=%b, b=%b", a_test, b_test);
                errors = errors + 1;
            end

            if (out_xnor !== expected_xnor) begin
                $display("Error: XNOR failed for a=%b, b=%b", a_test, b_test);
                errors = errors + 1;
            end

            if (out_anotb !== expected_anotb) begin
                $display("Error: ANOTB failed for a=%b, b=%b", a_test, b_test);
                errors = errors + 1;
            end

            testcases = testcases + 1;
        end
    endtask

    // Run tests
    initial begin
        $display("Starting test...");

        apply_test(0, 0, 0, 0, 0, 1, 1, 1, 1);  // Test #1
        apply_test(0, 1, 0, 1, 1, 1, 0, 0, 0);  // Test #2
        apply_test(1, 0, 0, 1, 1, 1, 0, 0, 1);  // Test #3
        apply_test(1, 1, 1, 1, 0, 0, 0, 1, 0);  // Test #4

        if (errors == 0) begin
            $display("All test cases passed.");
        end

        $finish;
    end
endmodule

