        .in(in), 
        .parity(parity)
    );

    // Test variables
    integer i;
    reg [7:0] expected_parity;
    reg test_failed = 0;

    // Calculate expected parity function
    function reg calc_parity(input [7:0] data);
        integer j;
        reg p;
        begin
            p = 0;
            for (j = 0; j < 8; j = j + 1)
                p = p ^ data[j];
            calc_parity = p;
        end
    endfunction

    // Test process
    initial begin
        // Test all possible 8-bit combinations
        for (i = 0; i < 256; i = i + 1) begin
            in = i;
            #10;  // Wait for changes to propagate

            expected_parity = calc_parity(in);
            if (parity !== expected_parity) begin
                test_failed = 1;
                $display("Failed at input: %b, Expected: %b, Got: %b", in, expected_parity, parity);
            end
        end

        if (test_failed === 0)
            $display("all test cases passed");
    end
