module testbench;
    reg clk;
    reg areset;
    reg bump_left;
    reg bump_right;
    reg ground;
    reg dig;
    wire walk_left;
    wire walk_right;
    wire aaah;
    wire digging;

    top_module DUT (
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .dig(dig),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah),
        .digging(digging)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = !clk; // Clock period of 10 ns
    end

    // Test cases
    initial begin
        // Initialize signals
        areset = 1; bump_left = 0; bump_right = 0; ground = 1; dig = 0;

        #10; // Wait for reset to propagate
        areset = 0;

        // Test 1: Check initial state (walking left)
        if (walk_left !== 1 || walk_right !== 0) $display("Test 1 Failed");

        // Test 2: Bump on left, should walk right
        bump_left = 1;
        #10; bump_left = 0;
        if (walk_left !== 0 || walk_right !== 1) $display("Test 2 Failed");

        // Test 3: Walk right and hit bump right, should walk left
        bump_right = 1;
        #10; bump_right = 0;
        if (walk_left !== 1 || walk_right !== 0) $display("Test 3 Failed");

        // Test 4: Both sides bump, should reverse direction (left to right)
        bump_left = 1; bump_right = 1;
        #10; bump_left = 0; bump_right = 0;
        if (walk_left !== 0 || walk_right !== 1) $display("Test 4 Failed");

        // Test 5: Ground disappears, should "aaah!" while falling
        ground = 0; // Start falling
        #10;
        if (!aaah) $display("Test 5 Failed");

        // Test 6: On ground again, continue in previous direction (right)
        ground = 1;
        #10;
        if (walk_left !== 0 || walk_right !== 1 || aaah !== 0) $display("Test 6 Failed");

        // Test 7: Start digging
        dig = 1;
        #10; dig = 0;
        if (!digging) $display("Test 7 Failed");

        // Test 8: Finish Digging, hit no ground, fall
        ground = 0;
        #10;
        if (!aaah || digging !== 0) $display("Test 8 Failed");

        // Test 9: Survive fall longer than 20 cycles (no splatter if less than 20 cycles)
        #210; // Stay in fall state for 210 ns (21 cycles)
        ground = 1;
        #10;
        if (walk_left !== 0 && walk_right !== 0) $display("Test 9 Failed"); // Should splatter, set outputs zero

        // No failed message, all tests passed
        if (!$test$plusargs("Failed")) $display("all test cases passed");

        $finish;
    end
endmodule

