    reg [7:0] d;
    reg areset;
    wire [7:0] q;

    top_module DUT (
        .clk(clk),
        .d(d),
        .areset(areset),
        .q(q)
    );

    // Clock generation
    always #5 clk = ~clk; // 100MHz Clock

    // Test cases
    initial begin
        // Initialize signals
        clk = 0;    
        d = 0;
        areset = 0;

        // Reset all flip-flops
        #1 areset = 1; #10 areset = 0;
        if (q !== 8'b00000000) begin
            $display("Test Reset Case Failed");
            $finish;
        end

        // Test input propagation after reset
        #10 d = 8'b10101010; // Random test data
        #10 if (q !== 8'b10101010) begin
            $display("Test Data Propagation Failed");
            $finish;
        end

        // Test asynchronous reset during operation
        #10 d = 8'b11111111; // New input while clock is running
        #5 areset = 1;
        #10 if (q !== 8'b00000000) begin
            $display("Test Async Reset Functionality Failed");
            $finish;
        end
        areset = 0;

        // Final test check: delay to check several clock edges
        #30 d = 8'b01010101;
        #10 if (q !== 8'b01010101) begin
            $display("Test Final Check Failed");
            $finish;
        end

        // If all tests pass
        $display("all test cases passed");
    end

endmodule

