    top_module dut (
        .clk(clk),
        .d(d),
        .reset(reset),
        .q(q)
    );

    // Clock Generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Clock period = 10ns
    end

    // Test Cases
    initial begin
        // Initialize Inputs
        d = 0;
        reset = 0;

        // Reset and check the value
        @(negedge clk) begin
            reset = 1;
            d = 8'hXX;  // X represents dont-care during reset
        end
        @(negedge clk) begin
            reset = 0;
            if (q !== 8'h34) begin
                $display("Reset to 0x34 failed on reset");
                $finish;
            end
        end

        // Test Case 1: Normal operation checking
        @(negedge clk) d = 8'hAA;
        @(negedge clk) begin
            if (q !== 8'hAA) begin
                $display("Failed to latch value 8'hAA");
                $finish;
            end
        end

        // Test Case 2: Reset during normal operation
        @(negedge clk) d = 8'h55;
        @(negedge clk) reset = 1;
        @(negedge clk) begin
            reset = 0;
            if (q !== 8'h34) begin
                $display("Failed to reset during operation to 0x34");
                $finish;
            end
        end

        // Test Case 3: Data stability
        @(negedge clk) d = 8'hFF;
        @(negedge clk) begin
            if (q !== 8'hFF) begin
                $display("Data stability failed at value 8'hFF");
                $finish;
            end
        end

        // If all test cases have passed
