module testbench;
    reg clk, a;
    wire q;
    reg[31:0] cycle;

    // Instantiate the DUT
    top_module dut(
        .clk(clk),
        .a(a),
        .q(q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Test vector generation and checking
    initial begin
        cycle = 0;
        a = 1'bx; // Unknown at start

        // Apply test vectors according to the problem description
        @(posedge clk); #1 a = 0;
        @(posedge clk); #1 a = 0;
        @(posedge clk); #1
        check_q(1, q);
        @(posedge clk); #1
        check_q(1, q);
        @(posedge clk); #1
        check_q(1, q);
        @(posedge clk); #1
        check_q(1, q);
        @(posedge clk); #1 a = 1;
        @(posedge clk); #1
        check_q(1, q);
        @(posedge clk); #1
        check_q(0, q);
        @(posedge clk); #1
        check_q(0, q);
        @(posedge clk); #1
        check_q(0, q);
        @(posedge clk); #1
        check_q(0, q);
        @(posedge clk); #1
        check_q(0, q);
        @(posedge clk); #1
        check_q(0, q);

        // Final report
        all_checks_passed;

        $finish;
    end

    // Check procedure
    task check_q(input expected, input actual);
        begin
            if (expected !== actual) begin
                $display("Test failed at time %t with cycle=%d: expected=%b, actual=%b", $time, cycle, expected, actual);
                disable all_checks_passed;
            end else begin
                $display("Test passed at time %t with cycle=%d: expected=%b, actual=%b", $time, cycle, expected, actual);
            end
            cycle = cycle + 1;
        end
    endtask

    // If all test cases passed
    task all_checks_passed;
        begin
            $display("all test cases passed");
        end
    endtask

endmodule

