module top_module_tb;

    // Inputs
    reg clk;
    reg reset;
    reg data;

    // Outputs
    wire start_shifting;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .reset(reset),
        .data(data),
        .start_shifting(start_shifting)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generate a clock with 10ns period
    end

    // Test Cases
    initial begin
        // Initialize Inputs
        reset = 1;
        data = 0;
        #10;

        reset = 0;
        #10; // let FSM start in stable state

        // Test Pattern 1: No 1101 sequence
        data = 0; #10;
        data = 1; #10;
        data = 0; #10;
        data = 0; #10;
        data = 1; #10; 
        if (start_shifting !== 0) begin
            $display("Test Case 1 Failed: Unexpected start_shifting without 1101 sequence");
            $finish;
        end

        // Test Pattern 2: Correct 1101 sequence
        data = 1; #10;
        data = 1; #10;
        data = 0; #10;
        data = 1; #10;
        if (start_shifting !== 1) begin
            $display("Test Case 2 Failed: start_shifting not asserted after 1101 sequence");
            $finish;
        end

        // Test Pattern 3: Reset test
        reset = 1; #10;
        if (start_shifting !== 0) begin
            $display("Test Case 3 Failed: start_shifting not cleared after reset");
            $finish;
        end

        reset = 0; #10;
        data = 1; #10;
        data = 1; #10;
        data = 0; #10;
        data = 1; #10;
        if (start_shifting !== 1) begin
            $display("Test Case 4 Failed: start_shifting not asserted after 1101 and reset");
            $finish;
        end

        $display("all test cases passed");
    end

endmodule

