module tb_JKFlipFlop;

    // Inputs
    reg clk;
    reg j;
    reg k;

    // Output
    wire Q;

    // Instantiate the DUT
    top_module DUT (
        .clk(clk),
        .j(j),
        .k(k),
        .Q(Q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #10 clk = ~clk;
    end

    // Test sequences
    initial begin
        reg QafterToggle; // Declare the register at the top of block

        // Initialize Inputs
        j = 0;
        k = 0;

        // Test Case 1: State 0,0 -> Qold
        #20;  // Wait for first positive edge of clock
        if (Q !== 0) $display("Test Case Failed at State 0,0 with Q=%b", Q);

        // Test Case 2: State 0,1 -> Q=0
        j = 0;
        k = 1;
        #20;  // Wait for positive clock edge
        if (Q !== 0) $display("Test Case Failed at State 0,1 with Q=%b", Q);

        // Test Case 3: State 1,0 -> Q=1
        j = 1;
        k = 0;
        #20;  // Wait for positive clock edge
        if (Q !== 1) $display("Test Case Failed at State 1,0 with Q=%b", Q);

        // Test Case 4: State 1,1 -> Q=~Qold (Toggle)
        j = 1;
        k = 1;
        #20;  // Wait for first positive edge of clock
        QafterToggle = ~Q;
        #20;  // Wait for second positive edge of clock
        if (Q !== QafterToggle) $display("Test Case Failed at State 1,1 with Q=%b", Q);

        // All test cases passed
        #10;  // Wait for all checks
        if (!$test$plusargs("fail")) $display("all test cases passed");

        $finish;
    end

endmodule

