module tb_top_module;

    reg clk;
    reg areset;
    reg load;
    reg ena;
    reg [3:0] data;
    wire [3:0] q;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .areset(areset),
        .load(load),
        .ena(ena),
        .data(data),
        .q(q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock with period 10ns
    end

    // Test stimulus
    initial begin
        // Initialize inputs
        areset = 0;
        load = 0;
        ena = 0;
        data = 4'b0;

        // Assertion of areset
        #10;
        areset = 1; // Reset the shift register
        #10;
        areset = 0;
        if (q !== 4'b0000) begin
            $display("Test Case failed: areset did not reset the shift register.");
            $finish;
        end

        // Loading data to shift register
        load = 1;
        data = 4'b1011;
        #10;
        load = 0;
        if (q !== 4'b1011) begin
            $display("Test Case failed: load did not load the correct data.");
            $finish;
        end

        // Enable shift operation
        ena = 1;
        #10;
        if (q !== 4'b0101) begin
            $display("Test Case failed: shift operation did not work correctly.");
            $finish;
        end

        // Test both load and ena asserted
        load = 1; data = 4'b1100;
        #10;
        if (q !== 4'b1100) begin
            $display("Test Case failed: load does not have priority over ena.");
            $finish;
        end

        load = 0;
        // More clock cycles to test repeated shifting
        #10;
        if (q !== 4'b0110) begin
            $display("Test Case failed: subsequent shift operation failed.");
            $finish;
        end

        #10;
        if (q !== 4'b0011) begin
            $display("Test Case failed: subsequent shift operation failed.");
            $finish;
        end

        #10;
        // Final test to ensure q has shifted correctly
        if (q == 4'b0001) begin
            $display("all test cases passed");
        end else begin
            $display("Test Case failed: final shift operation failed or incorrect initial shift logic.");
        end

        $finish;
    end

endmodule

