module testbench;
  reg clk, reset, s, w;
  reg [2:0] count_ones;
  wire z;

  // DUT instantiation
  top_module dut(clk, reset, s, w, z);

  // Clock generation
  initial begin
    clk = 0;
    forever #5 clk = !clk; // Generate a clock with a period of 10ns
  end

  // Test cases
  initial begin
    reset = 1; s = 0; w = 0; count_ones = 0;
    @(posedge clk);
    reset = 0;
    @(posedge clk);

    // Test case 1: Remain in State A
    s = 0; w = 0; // stay in state A
    repeat (5) @(posedge clk);
    if (z !== 0) begin
      $display("Test case 1 Failed");
      $finish;
    end

    // Test case 2: Transition to State B and check z = 0 scenarios
    s = 1; // transition to state B
    @(posedge clk);

    // Two cycles with w = 0, one cycle with w = 1
    w = 0; @(posedge clk);
    w = 1; @(posedge clk);
    w = 0; @(posedge clk);
    @(posedge clk);
    if (z !== 0) begin
      $display("Test case 2 Failed");
      $finish;
    end

    // Test case 3: z = 1 scenario
    // Three cycles: two cycles with w = 1, one with w = 0
    w = 1; @(posedge clk);
    w = 1; @(posedge clk);
    w = 0; @(posedge clk);
    @(posedge clk);
    if (z !== 1) begin
      $display("Test case 3 Failed");
      $finish;
    end

    // Test case 4: Check incorrect count of w=1
    w = 1; @(posedge clk);
    w = 0; @(posedge clk);
    w = 0; @(posedge clk);
    @(posedge clk);
    if (z !== 0) begin
      $display("Test case 4 Failed");
      $finish;
    end

    // Test case 5: Correct count of w=1 at different orders
    w = 0; @(posedge clk);
    w = 1; @(posedge clk);
    w = 1; @(posedge clk);
    @(posedge clk);
    if (z !== 1) begin
      $display("Test case 5 Failed");
      $finish;
    end

    $display("all test cases passed");
    $finish;
  end
endmodule

