        clk = 0;
        forever #5 clk = ~clk;  // Clock period of 10ns
    end

    // Testbench Logic
    initial begin
        // Initialize Inputs
        slowena = 0;
        reset = 0;

        // Reset the DUT
        #10;
        reset = 1;  // Assert reset
        #10;
        reset = 0;  // Deassert reset
        if (q !== 0) begin
            $display("Reset test failed at time %t", $time);
            $finish;
        end

        // Check counting functionality
        #10;
        slowena = 1;  // Enable counting
        repeat(10) begin
            #10;
