        #20;
        if (q !== 9) begin
            $display("Pause test failed, expected %d, got %d at time %t", 9, q, $time);
            $finish;
        end

        // Check if the counter wraps correctly from 9 to 0
        slowena = 1; 
        #10;
        if (q !== 0) begin
            $display("Wrap test failed, expected 0, got %d at time %t", q, $time);
            $finish;
        end

        // All tests passed
        $display("all test cases passed");

        // End simulation
        $finish;
    end

