module tb_top_module;

    // Inputs
    reg clk;
    reg reset;

    // Outputs
    wire [4:0] q;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .reset(reset),
        .q(q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Simulation parameters
    integer i;
    reg [4:0] expected_state;
    reg error_detected = 0;

    // Procedure to apply reset
    task apply_reset;
    begin
        reset = 1;
        #10;
        reset = 0;
    end
    endtask

    // Initialize Inputs and expected states
    initial begin
        // Reset the LFSR
        apply_reset();
        if (q !== 5'b00001) begin
            $display("Test failed at reset.");
            error_detected = 1;
        end

        // Test the sequence generation
        expected_state = 5'b00001;  // After reset
        for (i = 0; i < 31; i = i + 1) begin
            @(posedge clk);
            expected_state = {expected_state[3], expected_state[2], expected_state[1] ^ expected_state[4], expected_state[0] ^ expected_state[4], expected_state[4]};
            if (q !== expected_state) begin
                $display("Test failed at step %d: expected %b, got %b.", i, expected_state, q);
                error_detected = 1;

                // Corrected: Using the keyword disable followed by the correct block to terminate
                disable this;  // Stop the current block - 'initial'
            end
        end

        // After all tests passed
        if (error_detected == 0) begin
            $display("all test cases passed");
        end
    end

    // Stop simulation after some time
    initial begin
        #1000;
        $finish;
