module tb_top_module ();

reg clk;
reg reset;
reg s;
reg w;
wire z;

top_module UUT (
    .clk(clk),
    .reset(reset),
    .s(s),
    .w(w),
    .z(z)
);

// Clock generation
initial begin
    clk = 0;
    forever #5 clk = ~clk; // Clock period of 10 ns
end

// Reset operation
initial begin
    reset = 1;       // Assert reset
    #15 reset = 0;    // Release reset after 15 ns
end

reg [2:0] test_vector [0:7]; // Array to hold w inputs for testing
integer i, count_ones, errors;

initial begin
    s = 0;
    w = 0;
    errors = 0;
    // Initialize test vectors
    test_vector[0] = 3'b110; // z should be 1
    test_vector[1] = 3'b101; // z should be 1
    test_vector[2] = 3'b011; // z should be 1
    test_vector[3] = 3'b100; // z should be 0
    test_vector[4] = 3'b010; // z should be 0
    test_vector[5] = 3'b001; // z should be 0
    test_vector[6] = 3'b111; // z should be 0
    test_vector[7] = 3'b000; // z should be 0

    #20; // Wait for the reset to release

    // Begin testing cycles
    for (i = 0; i < 8; i = i + 1) begin
        @(posedge clk);
        s = 1;      // Change state from A to B
        w = test_vector[i][2];
        @(posedge clk);
        w = test_vector[i][1];
        @(posedge clk);
        w = test_vector[i][0];
        @(posedge clk);

        count_ones = test_vector[i][2] + test_vector[i][1] + test_vector[i][0];
        // Check the result in the next clock after loading inputs
        if ((count_ones == 2 && z !== 1) || (count_ones != 2 && z !== 0)) begin
            $display("Test case %d with vector %b failed: Expected z=%d, got z=%b", i, test_vector[i], (count_ones == 2), z);
            errors = errors + 1;
        end
        @(posedge clk);
        s = 0; // Return to state A
    end

    if (errors == 0) $display("All test cases passed");

    $finish;
end
endmodule

