        all_cases_passed = 1;
        clk = 0; // Initialize clock
        forever #5 clk = ~clk; // Generate clock with a period of 10 ns
    end

    initial begin
        // Test Case 1: Correct data transmission
        data_stream = 10'b1011001101; // Valid sequence: start(0) + "01100110" (LSB first) + stop(1)
        reset = 1;
        #10 reset = 0;
        for (i = 0; i < 10; i++) begin
            in = data_stream[i];
            #10;
        end
        if (!done) all_cases_passed = 0;

        // Test Case 2: Incorrect stop bit
        data_stream = 10'b1011001100; // Incorrect stop bit: start(0) + "01100110" + stop(0)
        reset = 1;
        #10 reset = 0;
        for (i = 0; i < 10; i++) begin
            in = data_stream[i];
            #10;
        end
        if (done) all_cases_passed = 0;

        // Test Case 3: Correct data after incorrect stop bit
        data_stream = 10'b1011001100; // First byte - bad stop bit
        for (i = 0; i < 10; i++) begin
            in = data_stream[i];
            #10;
        end
        data_stream = 10'b1011110001; // Second byte - valid
        for (i = 0; i < 10; i++) begin
            in = data_stream[i];
            #10;
        end
        if (!done) all_cases_passed = 0;

        // Test Case 4: Continuous valid data
        repeat (3) begin
            data_stream = 10'b1001010101; // Multiple valid packets
            for (i = 0; i < 10; i++) begin
                in = data_stream[i];
                #10;
            end
            if (!done) all_cases_passed = 0;
        end

        // Final result evaluation
        if (all_cases_passed)
            $display("all test cases passed");

        $finish;
    end

endmodule

