module testbench;
    reg in;
    reg [9:0] state;
    wire [9:0] next_state;
    wire out1, out2;
    integer i;

    top_module dut (
        .in(in),
        .state(state),
        .next_state(next_state),
        .out1(out1),
        .out2(out2)
    );

    initial begin
        // Test sequence
        $display("Starting test...");
        state = 10'b0000000001; // S0
        for (i = 0; i < 20; i = i + 1) begin
            #1;
            in = i % 2; // Alternate between 0 and 1
            #1;
            $display("Time: %0d | State: %b | in: %d | Next: %b | Out1: %d | Out2: %d", $time, state, in, next_state, out1, out2);

            case (state)
                10'b0000000001: if (!((in == 0 && next_state == 10'b0000000001) || (in == 1 && next_state == 10'b0000000010))) $finish;// S0
                10'b0000000010: if (!((in == 0 && next_state == 10'b0000000001) || (in == 1 && next_state == 10'b0000000100))) $finish;// S1
                10'b0000000100: if (!((in == 0 && next_state == 10'b0000000001) || (in == 1 && next_state == 10'b0000001000))) $finish;// S2
                10'b0000001000: if (!((in == 0 && next_state == 10'b0000000001) || (in == 1 && next_state == 10'b0000010000))) $finish;// S3
                10'b0000010000: if (!((in == 0 && next_state == 10'b0000000001) || (in == 1 && next_state == 10'b0000100000))) $finish;// S4
                10'b0000100000: if (!((in == 0 && next_state == 10'b0001000000) || (in == 1 && next_state == 10'b0000001000))) $finish;// S5
                10'b0001000000: if (!((in == 0 && next_state == 10'b0010000000) || (in == 1 && next_state == 10'b0100000000) || (out1 != 0) || (out2 != 0))) $finish;// S6
                10'b0100000000: if (!((in == 0 && next_state == 10'b0000000001 && out1 == 0 && out2 == 1) || (in == 1 && next_state == 10'b0100000000 && out1 == 0 && out2 == 1))) $finish;// S7
                10'b0010000000: if (!((in == 0 && next_state == 10'b0000000001 && out1 == 1 && out2 == 0) || (in == 1 && next_state == 10'b0000000010 && out1 == 1 && out2 == 0))) $finish;// S8
                10'b1000000000: if (!((in == 0 && next_state == 10'b0000000001 && out1 == 1 && out2 == 1) || (in == 1 && next_state == 10'b0000000010 && out1 == 1 && out2 == 1))) $finish;// S9
                default: $display("Invalid State Detected.");
            endcase

            state = next_state; // Move to the next state

        end

        // If all tests pass
        $display("all test cases passed");
        $finish;
    end

endmodule

