    wire walk_left, walk_right;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .walk_left(walk_left),
        .walk_right(walk_right)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Generate a clock with a period of 10ns
    end

    // Stimulus
    initial begin
        // Initialize inputs
        areset = 1; 
        bump_left = 0;
        bump_right = 0;

        // Asynchronous reset
        @(posedge clk)
        areset = 0;
        @(posedge clk);
        areset = 1;
        @(posedge clk);
        areset = 0;

        // Test Scenario 1: Walking left, getting bumped on left
        #10 bump_left = 1;
        @(posedge clk)
        if (walk_right !== 1'b1) $display("Test Case 1 Failed");
        bump_left = 0;

        // Test Scenario 2: Walking right, getting bumped on right
        #20 bump_right = 1;
        @(posedge clk)
        if (walk_left !== 1'b1) $display("Test Case 2 Failed");
        bump_right = 0;

        // Test Scenario 3: Getting bumped on both sides while walking left
        #30 bump_left = 1; bump_right = 1;
        @(posedge clk)
        if (walk_right !== 1'b1) $display("Test Case 3 Failed");
        bump_left = 0; bump_right = 0;

        // Test Scenario 4: Double check for switching back to correct direction
        #40 bump_right = 1;
