module testbench;

    reg clk;
    reg reset;
    reg data;
    reg ack;
    wire [3:0] count;
    wire counting;
    wire done;

    top_module DUT (
        .clk(clk),
        .reset(reset),
        .data(data),
        .count(count),
        .counting(counting),
        .done(done),
        .ack(ack)
    );

    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Generate a clock with 10ns period (100MHz)
    end

    task send_data(input [3:0] data_bits);
        integer i;
        for (i=3; i>=0; i=i-1) begin
            @(posedge clk);
            data <= data_bits[i];
        end
    endtask

    task reset_dut;
        begin
            @(posedge clk);
            reset <= 1;
            @(posedge clk);
            reset <= 0; 
        end
    endtask

    integer successful_tests, i;
    reg [3:0] delay_value;

    initial begin
        reset <= 0;
        data <= 0;
        ack <= 0;
        successful_tests = 0;
        reset_dut;
        $display("Start Testing...");

        // Test Case 1: Validate the Start and Delay Handling
        send_data(4'b1101);  // Start pattern
        delay_value = 4'd2;
        send_data(delay_value);  // Set delay
        #10000;  // Wait for complete delay
        if (done && count == 0) begin
            successful_tests = successful_tests + 1;
        end

        reset_dut;

        // Test Case 2: Test Done Signal and Acknowledgement
        send_data(4'b1101);  // Start pattern
        send_data(4'b0001);  // Set delay
        #5000;  // Wait for Done
        if (done && count == 0) begin
            ack <= 1;  // Set Acknowledge
            @(posedge clk);
            ack <= 0;
            successful_tests = successful_tests + 1;
        end

        reset_dut;

        // Test Case 3: Check Immediate Reset after Ack
        send_data(4'b1101);
        send_data(4'd1);
        #5000;  // Wait halfway through the count
        ack <= 1;  // Set Acknowledge to reset
        @(posedge clk);
        ack <= 0;
        @(posedge clk);
        if (!counting) begin
            successful_tests = successful_tests + 1;
        end

        reset_dut;

        // Complete all tests
        if (successful_tests == 3) begin
            $display("all test cases passed");
        end

        $finish;  // End simulation
    end

endmodule

