    .clk(clk),
    .resetn(resetn),
    .x(x),
    .y(y),
    .f(f),
    .g(g)
  );

  // Clock generation
  always #5 clk = ~clk;

  initial begin
    // Initialize inputs
    clk = 0;
    resetn = 0;
    x = 0;
    y = 0;

    // Reset the device
    #10;
    resetn = 1;
    #10;
    resetn = 0;
    #10;
    resetn = 1;

    // f should be 1 after reset de-asserted
    #10;
    if (f !== 1) $display("Test case failed: f is not 1 after reset.");

    // f should be 0 in the next cycle
    #10;
    if (f !== 0) $display("Test case failed: f is not 0 after being set to 1.");

    // Test sequence for g: x=1, x=0, x=1
    #10; x = 1;
    #10; x = 0;
    #10; x = 1;
    #10;
    if (g !== 1) $display("Test case failed: g is not 1 after sequence 1,0,1.");

    // Test y=0 twice, g should become 0
    #10; y = 0;
    #10; y = 0;
    #10;
    if (g !== 0) $display("Test case failed: g is not 0 after two y=0's.");

    // Reset and test y=1 within two cycles
    resetn = 0; x = 0; y = 0; 
    #10; resetn = 1;

    #40; x = 1; // Test x sequence again
    #10; x = 0;
    #10; x = 1;
    #10;
    if (g !== 1) $display("Test case failed: g is not 1 after second sequence 1,0,1.");

    #10; y = 0;
    #10; y = 1; // y becomes 1 within two cycles
    #10;
    if (g !== 1) $display("Test case failed: g is not maintained at 1.");

    // If no test case failed, display all tests passed
    if (!$test$plusargs("Test case failed")) $display("all test cases passed.");

    #10 $finish;
  end

