        .clk(clk),
        .reset(reset),
        .w(w),
        .z(z)
    );

    // Clock generation
    always begin
        #5 clk = ~clk; // Toggle clock every 5ns
    end

    // Initial block to simulate test cases
    initial begin
        clk = 0;
        reset = 0;
        w = 0;
        all_passed = 1;
        test_id = 0;

        // Reset the device
        @(negedge clk) #1 reset = 1;
        @(negedge clk) #1 reset = 0;

        // Test 1: Stay in A on 0
        @(negedge clk) w = 0;
        @(negedge clk) if (z != 0) all_passed = 0;

        // Test 2: Transition A -> B on 1
        @(negedge clk) w = 1;
        @(negedge clk) w = 0;
        @(negedge clk) if (z != 0) all_passed = 0;

        // Test 3: Transition B -> D on 0
        @(negedge clk) w = 0;
        @(negedge clk) w = 0;
        @(negedge clk) if (z != 0) all_passed = 0;

        // Test 4: Transition D -> A on 0
        @(negedge clk) w = 0;
        @(negedge clk) w = 0;
        @(negedge clk) if (z != 0) all_passed = 0;

        // Test 5: Transition A -> B -> C on 1, 1
        @(negedge clk) w = 1;
        @(negedge clk) w = 1;
        @(negedge clk) if (z != 0) all_passed = 0;

        // Test 6: Transition C -> E on 1
        @(negedge clk) w = 1;
        @(negedge clk) w = 0;
        @(negedge clk) w = 0; // Confirm self loop E on 1 with z = 1
        @(negedge clk) if (z != 1) all_passed = 0;

        // Test 7: Transition F -> C on 1
        // Reset to state A
