        @(negedge clk) #1 reset = 0;
        @(negedge clk) w = 1; // A -> B
        @(negedge clk) w = 0; // B -> D
        @(negedge clk) w = 1; // D -> F
        @(negedge clk) if (z != 1) all_passed = 0;
        @(negedge clk) w = 1; // F -> C
        @(negedge clk) if (z != 0) all_passed = 0;

