    #10;

    // Test Case: Check all cells default when no load
    load = 1'b1;
    data = 512'b0;
    #10;
    load = 1'b0;
    #100;

    if (q !== 512'b0) begin
        $finish;
    end

    // Test Case: Boundary cells integration
    // Expecting to propagate only inner changes
    load = 1;
    // All zeros except boundary-adjacent cells are ones
    data[0] = 1;
    data[1] = 1;
    data[510] = 1;
    data[511] = 1;
    #10;
    load = 0;
    #10; //wait for the next clock to get updated result

    // Expectation based on rules, only inner cells should propagate
    // rest should remain zero due to zero boundary
    for (i = 0; i < 512; i = i + 1) begin
        if ((i > 1 && i < 510 && q[i] !== 1)) begin
            $finish;
        end
        if ((i <= 1 || i >= 510) && q[i] !== 0) begin
            $finish;
        end
    end

    // Test Case: Testing propagation over multiple cells
    load = 1;
    data = {1'b1, 510'b0, 1'b1};
    #10;
    load = 0;
    #10; //wait for the next clock to get updated result

    // Expectation: Cells should evolve according to Rule 110 from non-zero state
    if (q[2] !== 1 || q[509] !== 1) begin
        $finish;
    end

    // All test cases passed
    $display("all test cases passed");
    $finish;
end

endmodule

