    top_module dut (
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah)
    );

    // Clock generation
    always #10 clk = ~clk; // 50MHz clock

    // Test cases
    initial begin
        // Initialize Inputs
        clk = 0;
        areset = 0;
        bump_left = 0;
        bump_right = 0;
        ground = 1;

        // Reset sequence
        #5 areset = 1;
        #20 areset = 0;

        // Check initial state (should start walking left)
        #10 if (!(walk_left == 1 && walk_right == 0 && aaah == 0)) $display("Test failed: Initial state incorrect.");

        // Hit on the left should make it walk right
        bump_left = 1;
        #20 bump_left = 0;
        #10 if (!(walk_left == 0 && walk_right == 1 && aaah == 0)) $display("Test failed: Bump left transition incorrect.");

        // Hit on the right should make it walk left
        bump_right = 1;
        #20 bump_right = 0;
        #10 if (!(walk_left == 1 && walk_right == 0 && aaah == 0)) $display("Test failed: Bump right transition incorrect.");

        // Check falling
        ground = 0;
        #20 if (!(aaah == 1)) $display("Test failed: Fall detection incorrect.");

        // Recover from fall
        ground = 1;
        #20 if (!(walk_left == 1 && walk_right == 0 && aaah == 0)) $display("Test failed: Ground recovery incorrect.");

        // Check being bumped while falling
        ground = 0;
        bump_left = 1;
        bump_right = 0;
        #20 bump_left = 0;
        ground = 1;
        #10 if (!(walk_left == 1 && walk_right == 0 && aaah == 0)) $display("Test failed: Bumped while falling.");

        // Final state check for passing all tests
        #100 if (walk_left === 1 && walk_right === 0 && aaah === 0) $display("all test cases passed");
        #100 $finish;
    end
