module testbench;
    reg clk;
    reg areset;
    reg x;
    wire z;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .areset(areset),
        .x(x),
        .z(z)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100 MHz Clock
    end

    // Test vectors and checking mechanism
    reg [31:0] test_vectors[0:999]; // Limit size to 1000 for the sake of example.
    integer i, err;

    // Generate the 2's complement of a binary number
    function [31:0] twos_complement;
        input [31:0] bin;
        integer k;
        begin
            twos_complement = ~bin + 1;
        end
    endfunction

    // Testbench stimuli
    initial begin
        areset = 1; 
        x = 0; 
        #10;
        areset = 0; // Release the reset

        // Define test cases (Each number MUST start with least significant bit)
        test_vectors[0] = 32'b00000001; // 1
        test_vectors[1] = 32'b00000010; // 2
        test_vectors[2] = 32'b11111111; // -1 (255 in unsigned but -1 in signed 2's complement for 8 bits)
        err = 0;

        for (i = 0; i < 3; i = i + 1) begin
            integer bit_idx;
            for (bit_idx = 0; bit_idx < 32; bit_idx = bit_idx + 1) begin
                x = test_vectors[i][bit_idx];
                #10; // Next clock edge
            end
            #10;
            areset = 1; // Assert reset to process next number
            #10;
            areset = 0; // Release reset for next input

            // Check result
            if (z !== twos_complement(test_vectors[i])) begin
                err = 1;
            end
        end
        if (err == 0) begin
            $display("all test cases passed");
        end
        $finish;
    end
endmodule

