module testbench;
    reg clk;
    reg areset;
    reg bump_left;
    reg bump_right;
    reg ground;
    reg dig;
    wire walk_left;
    wire walk_right;
    wire aaah;
    wire digging;

    top_module DUT (
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .dig(dig),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah),
        .digging(digging)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // 100 MHz clock (period = 10 ns)
    end

    integer errors = 0;
    initial begin
        // Initialize inputs
        areset = 1; bump_left = 0; bump_right = 0; ground = 1; dig = 0;
        #10;
        areset = 0;

        // Test case 1: Walk left to right when bumped on the left.
        ground = 1; bump_left = 1; bump_right = 0;
        #10;
        if (!walk_right) begin
            $display("Test case 1 failed.");
            errors = errors + 1;
        end

        // Test case 2: Keep walking right, no bumps.
        bump_left = 0;
        #10;
        if (!walk_right) begin
            $display("Test case 2 failed.");
            errors = errors + 1;
        end

        // Test case 3: Change to walk left when bumped on the right.
        bump_right = 1;
        #10;
        if (!walk_left) begin
            $display("Test case 3 failed.");
            errors = errors + 1;
        end

        // Test case 4: Fall when ground disappears.
        ground = 0; bump_right = 0;
        #10;
        if (!aaah) begin
            $display("Test case 4 failed.");
            errors = errors + 1;
        end

        // Test case 5: Resume walking left when ground reappears.
        ground = 1;
        #10;
        if (!walk_left) begin
            $display("Test case 5 failed.");
            errors = errors + 1;
        end

        // Test case 6: Start digging while walking left.
        dig = 1;
        #10;
        if (!digging) begin
            $display("Test case 6 failed.");
            errors = errors + 1;
        end

        // Test case 7: Continue walking left after digging.
        dig = 0; ground = 0;  // Digging completes, Lemming falls
        #10;
        ground = 1;
        #10;
        if (!walk_left) begin
            $display("Test case 7 failed.");
            errors = errors + 1;
        end

        // Check if all test cases passed
        if (errors == 0) begin
            $display("all test cases passed");
        end
        $finish;
    end
endmodule

