top_module UUT (
	.in(t_in),
	.sel(t_sel),
	.out(t_out)
);

// Task to initialize inputs
initial begin
	// Initialize inputs
	t_in = 0;
	t_sel = 0;

	// Reset inputs for a clean start
	#100; // wait 100 ns for global reset to finish
	t_in = 256'h0;
	t_sel = 8'd0;
end

// Test cases
integer i;
reg all_tests_passed;
initial begin
	all_tests_passed = 1;
	for (i = 0; i < 256; i = i + 1) begin
		t_in = 1 << i;  // Set the i-th bit
		t_sel = i;      // Select the i-th bit
		#10;            // Wait for some time

		// Check if the output is the expected i-th bit value
		if (t_out !== t_in[i]) begin
			$display("Test case failure at t_sel = %d, t_out = %d, expected %d", t_sel, t_out, t_in[i]);
			all_tests_passed = 0;
		end
	end

	// If all test cases passed, display the message
	if (all_tests_passed) begin
		$display("all test cases passed");
	end

	$finish; // End the simulation
end

