        resetn = 1;
        #10;

        // Test Case 1: Reset condition - should be in state A
        if (g != 3'b000) begin
            $display("Test Case 1 Failed");
            $finish;
        end

        // Test case 2: Device 1 requests
        r = 3'b100; // r1=1, r2=0, r3=0
        #10;
        if (g != 3'b100) begin
            $display("Test Case 2 Failed");
            $finish;
        end

        // Test case 3: Device 1 drops, device 2 requests
