            $display("Test Case 3 Failed");
            $finish;
        end

        // Test case 4: Both devices 2 and 3 request while in state A
        r = 3'b011; // r1=0, r2=1, r3=1
        #10;
        if (g != 3'b010) begin
            $display("Test Case 4 Failed");
            $finish;
        end

        // Test case 5: Return to state A with no requests
        r = 3'b000; // All requests dropped
        #10;
        if (g != 3'b000) begin
            $display("Test Case 5 Failed");
            $finish;
        end

        // Test case 6: Device 3 requests when no other requests are present
        r = 3'b001; // r3=1, others are 0
        #20; // Allow for state change
