    // Inputs
    reg clk;
    reg in;
    reg reset;

    // Outputs
    wire [7:0] out_byte;
    wire done;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .in(in),
        .reset(reset),
        .out_byte(out_byte),
        .done(done)
    );

    // Clock generation
    always #5 clk = ~clk; // Create a clock with a period of 10ns

    initial begin
        // Initialize Inputs
        clk = 0;
        in = 1; // idle state, line held at logic 1
        reset = 1;

        // Reset the DUT
        #10; // wait for some time more than the period of the clock
        reset = 0;
        #10;
        reset = 1;
        #10;

        // Apply test cases
        // Case 1: Correct sequence with correct stop bit
        send_byte(8'b10101010); // Sending 0xAA, least significant bit first
        #100; // Wait enough time to process the byte

        // Case 2: Incorrect stop bit
        send_byte_with_wrong_stop(8'b01010101); // Sending 0x55, least significant bit first

        // Case 3: Correct sequence back to back
        send_byte(8'b11110000); // Sending 0x0F
        send_byte(8'b00001111); // Sending 0xF0
        #100;

        // Case 4: Random long wait then valid data
        #(1000*10) send_byte(8'b11001100); // Sending 0x33
        #100;

        // Check if all test cases passed and report
        if (test_passed)
            $display("all test cases passed");

        // Finish simulation
        $finish;
    end

    reg test_passed = 1'b1;
    task send_byte;
        input [7:0] byte;
        integer i;
        begin
            // Start bit
            in = 0;
            #(10);
            // Send byte LSB first
            for (i = 0; i < 8; i = i + 1) begin
                in = byte[i];
                #(10);
            end
            // Stop bit
            in = 1;
            #(10);
            in = 1; // go back to idle
        end
    endtask

    task send_byte_with_wrong_stop;
        input [7:0] byte;
        integer i;
        begin
            // Start bit
            in = 0;
            #(10);
            // Send byte LSB first
            for (i = 0; i < 8; i = i + 1) begin
                in = byte[i];
                #(10);
            end
            // Incorrect stop bit
            in = 0;
            #(10);
            in = 1; // go back to idle
            test_passed = 1'b0; // Set test to fail
        end
    endtask  
endmodule

