module testbench;
  reg [7:0] in;
  wire parity;

  // Instantiate the Device Under Test (DUT)
  top_module UUT (
    .in(in), 
    .parity(parity)
  );

  // Test variables
  integer i;
  reg [7:0] test_vector[0:255];  // Storing all possible 8-bit values
  reg calculated_parity;
  reg error_flag = 0;

  // Calculate parity function
  function reg compute_even_parity(input [7:0] data);
    integer j;
    reg result;
    begin
      result = 0;
      for (j = 0; j < 8; j = j + 1) begin
        result = result ^ data[j];
      end
      compute_even_parity = result;
    end
  endfunction

  // Initialize test vector
  initial begin
    for (i = 0; i < 256; i = i + 1) begin
      test_vector[i] = i;
    end
  end

  // Test procedure
  initial begin
    // Apply each test vector
    for (i = 0; i < 256; i = i + 1) begin
      in = test_vector[i];
      #10;  // Wait for the output to stabilize

      calculated_parity = compute_even_parity(in);

      // Check if the DUT output matches the calculated parity
      if (parity !== calculated_parity) begin
        $display("Test failed for input %b: expected %b, got %b", in, calculated_parity, parity);
        error_flag = 1;
      end
    end

    // Check if all test cases passed
    if (!error_flag) begin
      $display("all test cases passed");
    end

    $finish;
  end
endmodule

