                        expected_q[3:0] = 0;
                        expected_q[7:4] = expected_q[7:4] + 1;
                    end
                    if (expected_q[7:4] == 10) begin
                        expected_q[7:4] = 0;
                        expected_q[11:8] = expected_q[11:8] + 1;
                    end
                    if (expected_q[11:8] == 10) begin
                        expected_q[11:8] = 0;
                        expected_q[15:12] = expected_q[15:12] + 1;
