        correct_sequence = {correct_sequence[3], correct_sequence[2], correct_sequence[1], correct_sequence[0], (correct_sequence[4] ^ correct_sequence[2])}; // Perform the LFSR logic with taps at bit positions 5 and 3
      end
    end
  endfunction

