module testbench;
    reg a, b, c, d;
    wire out;

    top_module DUT (
        .a(a), 
        .b(b), 
        .c(c), 
        .d(d), 
        .out(out)
    );

    // Monitor inputs and outputs
    initial begin
        $monitor("Time=%t | a=%b, b=%b, c=%b, d=%b | output=%b", $time, a, b, c, d, out);
    end

    // Variable to keep track of test cases status
    reg all_tests_passed;

    // Initialize variables and run tests
    initial begin
        all_tests_passed = 1'b1;

        // Test cases based on the Karnaugh map
        // Format: a, b, c, d -> expected_out

        // cd=00
        test_case(0, 0, 0, 0, 1);
        test_case(0, 0, 0, 1, 1);
        test_case(0, 0, 1, 1, 0);
        test_case(0, 0, 1, 0, 1);

        // cd=01
        test_case(0, 1, 0, 0, 1);
        test_case(0, 1, 0, 1, 0);
        test_case(0, 1, 1, 1, 0);
        test_case(0, 1, 1, 0, 1);

        // cd=11
        test_case(1, 1, 0, 0, 0);
        test_case(1, 1, 0, 1, 1);
        test_case(1, 1, 1, 1, 1);
        test_case(1, 1, 1, 0, 1);

        // cd=10
        test_case(1, 0, 0, 0, 1);
        test_case(1, 0, 0, 1, 1);
        test_case(1, 0, 1, 1, 0);
        test_case(1, 0, 1, 0, 0);

        // Check and display test results
        #5; // Delay to ensure the last test completes
        if (all_tests_passed) begin
            $display("all test cases passed");
        end
        $finish;
    end

    // Task to handle individual tests
    task test_case;
        input reg ta, tb, tc, td; // Inputs for this test
        input reg expected_out; // Expected output for this test
        begin
            a = ta; b = tb; c = tc; d = td;
            #1; // Wait for the input changes to propagate
            if (out !== expected_out) begin
                $display("Test case failed: a=%b, b=%b, c=%b, d=%b, expected=%b, got=%b",
                         ta, tb, tc, td, expected_out, out);
                all_tests_passed = 1'b0;
            end
        end
    endtask

endmodule

