    integer i;
    reg [1:0] expected_pos;
    reg all_tests_passed = 1'b1;

    // Procedure to convert expected position
    task automatic get_expected_position;
        input [3:0] vector;
        output [1:0] position;
        integer j;
        begin
            position = 2'd0;
            for (j = 0; j < 4; j = j + 1) begin
                if (vector[j] == 1'b1) begin
                    position = j;
                    disable get_expected_position; // Exit after the first match
                end
            end
        end
    endtask

    // Test Cases 
    initial begin
        // Test all input combinations
        for (i = 0; i < 16; i = i + 1) begin
            in = i;
            get_expected_position(i, expected_pos);

            #10; // Wait for the DUT to process the input

            // Check the result
            if (pos !== expected_pos) begin
                all_tests_passed = 1'b0;
            end
        end

        #10; // Additional delay to ensure last check is processed
        if(all_tests_passed) begin
            $display("all test cases passed");
        end

        $finish;
    end

