module testbench;
    reg clk, reset, in;
    wire disc, flag, err;
    integer i;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .clk(clk),
        .reset(reset),
        .in(in),
        .disc(disc),
        .flag(flag),
        .err(err)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock with a period of 10ns
    end

    // Test cases
    initial begin
        reset = 1;
        in = 0;
        #10;
        reset = 0;

        // Test case 1: No flags, no error, no discard
        // Sending 00100
        send_bits(5'b00100, 5);
        assert_outputs(0, 0, 0);

        // Test case 2: Discard 0111110
        // Sending 0111110
        send_bits(7'b0111110, 7);
        assert_outputs(1, 0, 0);

        // Test case 3: Flag 01111110
        // Sending 01111110
        send_bits(8'b01111110, 8);
        assert_outputs(0, 1, 0);

        // Test case 4: Error 011111110
        // Sending 011111110
        send_bits(9'b011111110, 9);
        assert_outputs(0, 0, 1);

        // Test case 5: Combined sequence
        // Sending 0111110111111011111110
        send_bits(21'b011111011111101111111, 21); // Fixed bit width to match the intended size
        // Expect Discard, Flag, Error
        assert_outputs(1, 1, 1);

        if (test_error)
            $display("Some Test cases failed.");
        else
            $display("All test cases passed");

        $finish;
    end

    // Helper to send bits
    task send_bits;
        input [31:0] bits;
        input integer length;
        integer j;
        begin
            for (j=0; j<length; j=j+1) begin
                in = bits[j];
                #10;
            end
        end
    endtask

    // Helper to verify outputs
    reg test_error = 0;
    task assert_outputs;
        input exp_disc, exp_flag, exp_err;
        begin
            #10; // Wait for one clock cycle (output assertion delay)
            if (disc !== exp_disc || flag !== exp_flag || err !== exp_err) begin
                $display("Error: Expecting disc=%b, flag=%b, err=%b. Got disc=%b, flag=%b, err=%b at time %t",
                         exp_disc, exp_flag, exp_err, disc, flag, err, $time);
                test_error = 1;
            end
        end
    endtask

endmodule

