module testbench;
    reg clk;
    reg resetn;
    reg in;
    wire out;

    // Instantiate the DUT
    top_module DUT (
        .clk(clk),
        .resetn(resetn),
        .in(in),
        .out(out)
    );

    // Clock generation
    always #5 clk = ~clk;

    integer i;
    reg [3:1] shift_reg;

    initial begin
        clk = 1; // Default clock state
        resetn = 0; // Begin with reset active
        in = 0;
        shift_reg = 3'b000;

        // Reset the DUT
        #10 resetn = 1; // deactivate reset
        #10 resetn = 0; // apply reset
        #10 resetn = 1; // release reset

        // Verify that output is zero after reset
        if (out !== 0) begin
            $display("Test failed: output is not 0 after reset");
            $finish;
        end

        // Test shifting functionality
        for (i = 0; i < 4; i = i + 1) begin
            in = i % 2; // Generate pattern 0, 1, 0, 1
            shift_reg = {shift_reg[2:1], in}; // Expected shifted pattern
            #10; // Wait for one clock cycle
            if (out !== shift_reg[1]) begin // Check if output matches the expected bit
                $display("Test failed: shift functionality mismatch at cycle %d", i);
                $finish;
            end
        end

        // Reset and test output zero again
        #10 resetn = 0;
        #10 resetn = 1;
        if (out !== 0) begin
            $display("Test failed: output is not 0 after second reset");
            $finish;
        end

        // If no failures, pass all tests
        $display("all test cases passed");
        $finish;
    end

endmodule

