module testbench;
    reg clk, in, reset;
    wire out;
    reg [1:0] state;
    reg [1:0] next_state;
    reg error_flag;

    // Instantiate the DUT
    top_module DUT (
        .clk(clk),
        .in(in),
        .reset(reset),
        .out(out)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Clock with period of 10ns
    end

    // Stimulus
    initial begin
        error_flag = 0;
        reset = 1;    // Assert reset
        in = 0;       // Initial input value
        #15;          // Wait for reset to occur
        reset = 0;    // De-assert reset
        #10;

        // Transition from B (reset state) with in=0, expecting to go to A
        in = 0;
        #10;
        if(out !== 0) begin
            error_flag = 1;
        end

        // Now in state A with in=0 should go back to B
        in = 0;
        #10;
        if(out !== 1) begin
            error_flag = 1;
        end

        // Now still in state B with in=1 should stay in B
        in = 1;
        #10;
        if(out !== 1) begin
            error_flag = 1;
        end

        // Now still in state B with in=0 should transition to A
        in = 0;
        #10;
        if(out !== 0) begin
            error_flag = 1;
        end

        // Now in state A with in=1 should stay in A
        in = 1;
        #10;
        if(out !== 0) begin
            error_flag = 1;
        end

        // Check all test cases
        if(error_flag == 0) begin
            $display("all test cases passed");
        end

        $finish;
    end
endmodule

