module testbench;
    // Inputs
    reg in;
    reg [1:0] state;

    // Outputs
    wire [1:0] next_state;
    wire out;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .in(in),
        .state(state),
        .next_state(next_state),
        .out(out)
    );

    // Test cases
    initial begin
        // Initialize inputs
        in = 0;
        state = 2'b00; #10; // State A, input 0
        if (next_state != 2'b00 || out != 0) $display("Test case failed.");

        state = 2'b01; #10; // State B, input 0
        if (next_state != 2'b10 || out != 0) $display("Test case failed.");

        state = 2'b10; #10; // State C, input 0
        if (next_state != 2'b00 || out != 0) $display("Test case failed.");

        state = 2'b11; #10; // State D, input 0
        if (next_state != 2'b10 || out != 1) $display("Test case failed.");

        in = 1;
        state = 2'b00; #10; // State A, input 1
        if (next_state != 2'b01 ||  out != 0) $display("Test case failed.");

        state = 2'b01; #10; // State B, input 1
        if (next_state != 2'b01 || out != 0) $display("Test case failed.");

        state = 2'b10; #10; // State C, input 1
        if (next_state != 2'b11 || out != 0) $display("Test case failed.");

        state = 2'b11; #10; // State D, input 1
        if (next_state != 2'b01 || out != 1) $display("Test case failed.");

        $display("all test cases passed");
    end

endmodule

