module testbench;
    reg [7:0] code;
    wire [3:0] out;
    wire valid;
    reg all_pass;

    top_module dut(
        .code(code),
        .out(out),
        .valid(valid)
    );

    initial begin
        // Initialize
        all_pass = 1'b1;
        code = 8'h00;

        // Test case for key '0' (8'h45)
        #10 code = 8'h45;
        #10 if (!(out == 0 && valid == 1)) all_pass = 1'b0;

        // Test case for key '1' (8'h16)
        #10 code = 8'h16;
        #10 if (!(out == 1 && valid == 1)) all_pass = 1'b0;

        // Test case for key '2' (8'h1e)
        #10 code = 8'h1e;
        #10 if (!(out == 2 && valid == 1)) all_pass = 1'b0;

        // Test case for key '3' (8'h26)
        #10 code = 8'h26;
        #10 if (!(out == 3 && valid == 1)) all_pass = 1'b0;

        // Test case for key '4' (8'h25)
        #10 code = 8'h25;
        #10 if (!(out == 4 && valid == 1)) all_pass = 1'b0;

        // Test case for key '5' (8'h2e)
        #10 code = 8'h2e;
        #10 if (!(out == 5 && valid == 1)) all_pass = 1'b0;

        // Test case for key '6' (8'h36)
        #10 code = 8'h36;
        #10 if (!(out == 6 && valid == 1)) all_pass = 1'b0;

        // Test case for key '7' (8'h3d)
        #10 code = 8'h3d;
        #10 if (!(out == 7 && valid == 1)) all_pass = 1'b0;

        // Test case for key '8' (8'h3e)
        #10 code = 8'h3e;
        #10 if (!(out == 8 && valid == 1)) all_pass = 1'b0;

        // Test case for key '9' (8'h46)
        #10 code = 8'h46;
        #10 if (!(out == 9 && valid == 1)) all_pass = 1'b0;

        // Test case for an invalid code
        #10 code = 8'h55;
        #10 if (!(out == 0 && valid == 0)) all_pass = 1'b0;

        // Finally, test all scenario pass
        #10 if (all_pass) $display("all test cases passed");

        // End simulation
        #10 $finish;
    end
endmodule

