module tb_top_module;

reg [3:1] y;
reg w;
wire Y2;

// Instantiate the Device Under Test (DUT)
top_module DUT (
    .y(y), 
    .w(w), 
    .Y2(Y2)
);

// Testbench Variables
integer i;
integer failed = 0;

// Utility task to apply stimulus and check output
task apply_and_check;
    input [3:1] test_y;
    input test_w;
    input expected_y2;
    begin
        y = test_y;
        w = test_w;
        #10; // Allow some time for output to stabilize

        if (Y2 !== expected_y2) begin
            $display("Test case failed for y=%b and w=%b: Expected Y2=%b, Got Y2=%b", test_y, test_w, expected_y2, Y2);
            failed = 1;
        end
    end
endtask

// Initial block to run test cases
initial begin
    // Test Cases based on the provided state transition diagram
    apply_and_check(3'b000, 0, 0); // State A, w=0 -> State B, expect Y2=0
    apply_and_check(3'b000, 1, 0); // State A, w=1 -> State A, expect Y2=0
    apply_and_check(3'b001, 0, 0); // State B, w=0 -> State C, expect Y2=0
    apply_and_check(3'b001, 1, 1); // State B, w=1 -> State D, expect Y2=1
    apply_and_check(3'b010, 0, 1); // State C, w=0 -> State E, expect Y2=1
    apply_and_check(3'b010, 1, 1); // State C, w=1 -> State D, expect Y2=1
    apply_and_check(3'b011, 0, 0); // State D, w=0 -> State F, expect Y2=0
    apply_and_check(3'b011, 1, 0); // State D, w=1 -> State A, expect Y2=0
    apply_and_check(3'b100, 0, 0); // State E, w=0 -> State E, expect Y2=0
    apply_and_check(3'b100, 1, 1); // State E, w=1 -> State D, expect Y2=1
    apply_and_check(3'b101, 0, 0); // State F, w=0 -> State C, expect Y2=0
    apply_and_check(3'b101, 1, 1); // State F, w=1 -> State D, expect Y2=1

    if (failed == 0) begin
        $display("all test cases passed");
    end

    $finish;
end

endmodule

