module testbench;
    reg clk, reset, w;
    wire z;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .clk(clk),
        .reset(reset),
        .w(w),
        .z(z)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100MHz clock
    end

    // Test cases
    initial begin
        reset = 1; w = 0; #10;
        reset = 0; 

        // Test case 1: A (default) -> A
        w = 1; #10; check("A", z, 0);
        w = 0; #10; check("A", z, 0);

        // Test case 2: A -> B (on w=0) -> C (on w=0)
        reset = 1; #10;
        reset = 0; w = 0; #10;
        check("B", z, 0);
        w = 0; #10; check("C", z, 0);

        // Test case 3: A -> B (on w=0) -> D (on w=1)
        reset = 1; #10;
        reset = 0; w = 0; #10;
        check("B", z, 0);
        w = 1; #10; check("D", z, 0);

        // Test case 4: Loop test in E and exit to D
        reset = 1; #10;
        reset = 0; // A
        w = 0; #10; // B
        w = 0; #10; // C
        w = 0; #10; // E
        w = 0; #10; check("E", z, 1); // Remain in E
        w = 1; #10; check("D", z, 0); // Go to D

        // Test case 5: C -> D -> A
        reset = 1; #10;
        reset = 0; w = 0; #10; // B
        w = 0; #10; // C
        w = 1; #10; // D
        w = 1; #10; check("A", z, 0); // Return to A

        if (total_checks == passed_checks)
            $display("all test cases passed");

        $finish;
    end

    integer total_checks = 0, passed_checks = 0;
    task check;
        input [8*1-1:0] state;
        input exp_z, act_z;
        begin
            total_checks = total_checks + 1;
            if (exp_z !== act_z) begin
                $display("Test failed in state %s: Expected z=%b, got z=%b", state, exp_z, act_z);
            end else begin
                passed_checks = passed_checks + 1;
            end
        end
    endtask

endmodule

